$(function() {
    $(".J_menuItem").each(function(k) {
        if (!$(this).attr("data-index")) {
            $(this).attr("data-index", k)
        }
    });
    function c() {
        var o = $(this).attr("href"),
        m = $(this).data("index"),
        l = $.trim($(this).text()),
		z = "";
		$("#side-menu .J_menuItem").each(function() {
			$(this).parent().removeClass('active');
		});
		$(this).parent().addClass('active');
		$("#side-menu .active>a").each(function() {
			z += "<li><strong>" + $.trim($(this).text()) + "</strong></li>";
		});
        if (o == undefined || $.trim(o).length == 0) {
			  return false
        }
		$(".J_iframe").attr('src', o);
		$(".breadcrumb").html(z);
        return false
    }
    $(".J_menuItem").on("click", c); 
});